import React, { useState } from 'react';
import Papa from 'papaparse'

const DataCollecting = () => {

    const [data, setData] = useState([]);
    const [key, setKeys] = useState([]);
    const [file, setFile] = useState('');

    const FileChange = (e) => {
        //Takes the name of the file
        if (e.target.files.length) {
            const inputFile = e.target.files[0];

        //Takes the format after spliting the 'text/{type of file} and checks if is 'Cvs'
        const fileExtension = inputFile?.type.split("/")[1];
        if ( fileExtension !== 'csv') {
            alert('Input csv file !');
        }else{
            alert('correct');
        }
        setFile(inputFile);
    }
}

    const ShowData = () => {

        const reader = new FileReader();

        reader.onload = async ({ target }) => {
            const csv = Papa.parse(target.result, { header: true });
            const parsedData = csv?.data;
            const colums = Object.keys(parsedData)
            const rows = Object.values(parsedData[1])
            setData(rows);
            setKeys(colums)
            console.log(data, key);
        };
        reader.readAsText(file);


    }

    return (
    <div>
        <label>Upload file to check</label>
        <input type='file' onChange={FileChange} accept='.csv'/>
        <button onClick={ShowData} >Show data</button>
        <div>
            {
                data.map((data) => 
                <div key={data}>
                    <p>{data}</p>
                  </div>)
            }
        </div>
    </div>
    )
}

export default DataCollecting;
