import React, { useState } from "react";
import Papa from "papaparse";
import Table from "react-bootstrap/Table";
import { Col, Button, Form } from "react-bootstrap";

import "./dataCollecting.css";

const DataCollecting = () => {
  const [data, setData] = useState([]);
  const [file, setFile] = useState("");

  const FileChange = (e) => {
    //Takes the name of the file
    if (e.target.files.length) {
      const inputFile = e.target.files[0];

      //Takes the format after spliting the 'text/{type of file} and checks if is 'Cvs'
      const fileExtension = inputFile?.type.split("/")[1];
      if (fileExtension !== "csv") {
        alert("Input csv file !");
      }
      setFile(inputFile);
    }
  };

  const ShowData = () => {
    //Parse the data in array
    Papa.parse(file, {
      header: true,
      complete: (results) => {
        setData(results.data);
      },
    });
  };

  const DaysWorkedCounter = (z, y) => {
    let display = 0;
    if (z === "") {
      console.log(0);
    } else if (y === "") {
      console.log(1);
    } else {
      const date = z.split("/").reverse();
      const date2 = y.split("/").reverse();
      let dateTo = 0;

      if (date2.includes("NULL")) {
        dateTo = new Date();
      } else {
        dateTo = new Date(date2[0], date2[1], date2[2]);
      }

      const dateFrom = new Date(date[0], date[1], date[2]);
      const daysCounted = Math.round(
        (dateTo.getTime() - dateFrom.getTime()) / (1000 * 3600 * 24)
      );
      display = daysCounted
    }
    return display;
  };
  console.log(DaysWorkedCounter(data[1].DateFrom, data[1].DateTo))
  //console.log(Math.round((data[1].DateFrom.split('/').reverse().getTime() - data[1].DateTo.split('/').reverse().getTime()) / (1000 * 3600 * 24)))


  const days = (x,y) =>{ 
    DaysWorkedCounter(x, y);}

//   function a() {
//     let display = 0;
//     if (data.DateFrom === "" || data.DateTo === "") {
//       console.log(0);
//     } else {
//       const date = data.DateFrom.split("/").reverse();
//       const date2 = data.DateTo.split("/").reverse();
//       let dateTo = 0;

//       if (date2.includes("NULL")) {
//         dateTo = new Date();
//       } else {
//         dateTo = new Date(date2[0], date2[1], date2[2]);
//       }

//       const dateFrom = new Date(date[0], date[1], date[2]);
//       const daysCounted = Math.round(
//         (dateTo.getTime() - dateFrom.getTime()) / (1000 * 3600 * 24)
//       );
//       display = daysCounted
//     }
//     return display;}


  return (
    <Col className="column g-0">
      <Form.Group controlId="formFile" className="mb-3">
        <Form.Label>Upload your CSV file</Form.Label>
        <Form.Control type="file" onChange={FileChange} accept=".csv" />
      </Form.Group>
      <Button
        className="button"
        variant="secondary"
        size="md"
        onClick={days(data.DateFrom, data.DateTo)}
      >
        Show data
      </Button>
      <Table striped bordered hover variant="secondary">
        <thead>
          <tr>
            <th>Employee ID </th>
            <th>ProjectID</th>
            <th>DataFrom</th>
            <th>DataTo</th>
            <th>DaysWorked</th>
          </tr>
        </thead>
        {data.map((data, index) => (
          <tbody key={index}>
            <tr>
              <td>{data.EmpID}</td>
              <td>{data.ProjectID}</td>
              <td>{data.DateFrom}</td>
              <td>{data.DateTo === "NULL" ? "Today" : data.DateTo}</td>
              <td>{days(data.DateFrom, data.DateTo)}</td>
            
            </tr>
          </tbody>
        ))}
      </Table>
    </Col>
  );
};

export default DataCollecting;
