import React, { useState } from 'react';
import Papa from 'papaparse'
import Table from 'react-bootstrap/Table';
import { Col, Button, Form } from 'react-bootstrap'

import './dataCollecting.css'

const DataCollecting = () => {

    const [data, setData] = useState([]);
    const [file, setFile] = useState('');

    const FileChange = (e) => {
        //Takes the name of the file
        if (e.target.files.length) {
            const inputFile = e.target.files[0];

        //Takes the format after spliting the 'text/{type of file} and checks if is 'Cvs'
        const fileExtension = inputFile?.type.split("/")[1];
        if ( fileExtension !== 'csv') {
            alert('Input csv file !');
        }
        setFile(inputFile);
    }
}

    const ShowData = () => {
        //Parse the data in array
        Papa.parse(file, {
            header: true,
            complete: results => {
              setData(results.data)
            },
          });
    }

    // const date1 = data[1].DateFrom.split('/')
    // const date3 = data[2].DateTo.split('/')
    // const dayTo = new Date(date3[2], date3[1], date3[0])
    // const day = new Date(date1[2], date1[1], date1[0])
 
    console.log(Math.round((data[1].DateFrom.split('/').reverse().getTime() - data[1].DateTo.split('/').reverse().getTime()) / (1000 * 3600 * 24)))

    return (
    <Col className='column g-0'>
        <Form.Group controlId="formFile" className="mb-3">
            <Form.Label>Upload your CSV file</Form.Label>
            <Form.Control type="file" onChange={FileChange} accept='.csv' />
        </Form.Group>
        <Button className='button' variant="secondary" size="md" onClick={ShowData}> Show data </Button>
        <Table striped bordered hover variant='secondary'>
        <thead>
        <tr>
          <th>Employee ID </th>
          <th>ProjectID</th>
          <th>DataFrom</th>
          <th>DataTo</th>
        </tr>
       </thead>
        {
            data.map((data, index) =>
            <tbody key={index}>
            <tr>
            <td>{data.EmpID}</td>
            <td>{data.ProjectID}</td>
            <td>{data.DateFrom}</td>
            <td>{ data.DateTo === 'NULL' ? 'Today' : data.DateTo }</td>
            </tr>
           </tbody>)
        }
        </Table>
    </Col>
    )
}

export default DataCollecting;
